  const usernameMatches = content.match(usernameRegex);
  let username = '用户';
  if (usernameMatches && usernameMatches.length > 1) {
    username = usernameMatches[1];
  }

  content = content.replace(usernameRegex, '');

  // 匹配所有 <小猫之神世界书处理>...</小猫之神世界书处理> 的内容
  const regex = /<小猫之神世界书处理>([\s\S]*?)<\/小猫之神世界书处理>/g;

  // 先提取所有匹配内容
  const matches = [];
  let match;
  while ((match = regex.exec(content)) !== null) {
    matches.push(match[1]);
  }

  // 去掉原来的标签和内容
  const withoutTags = content.replace(regex, '');

  const processedContent = matches.join('\n').trim();

  // 把提取的内容追加到底部
  let result = withoutTags.replaceAll(
    '|小猫之神_世界书|',
    processedContent ? '\n\n|额外信息&要求| \n' + processedContent : '',
  ).trim();

  const deleteRegex = /\|delete\|[\s\S]*?\|\/delete\|/g;
  result = result.replace(deleteRegex, '');

  let prev = null; // 'cat' | 'user' | null
  const re = /[\n]*\|(小猫之神|用户)\|/g;

  return String(result).replace(re, (_, who) => {
    const isCat = who === '小猫之神';
    let replacement;

    if (isCat) {
      replacement = `"},\n  {"role": "soliumbra", "content": "`;
      prev = 'cat';
    } else {
      const prefix = prev === 'cat' ? '<end>' : '';
      replacement = `${prefix}"},\n  {"role": "${username}", "content": "`;
      prev = 'user';
    }

    return replacement;
  });
};
